#include "vector_macros.h"

void TEST_CASE1(void) {
  VSET(16, e8, m1);
  VLOAD_8(v2, 0xff, 0x00, 0xf0, 0x0f, 0xff, 0x00, 0xf0, 0x0f, 0xff, 0x00, 0xf0, 0x0f, 0xff, 0x00, 0xf0, 0x0f);
  VLOAD_8(v3, 0xf2, 0x01, 0xf0, 0x0f, 0xf2, 0x01, 0xf0, 0x0f, 0xf2, 0x01, 0xf0, 0x0f, 0xf2, 0x01, 0xf0, 0x0f);
  asm volatile("vmset.m v1");
  asm volatile("vmseq.vv v1, v2, v3");
  VSET(2, e8, m1);
  VCMP_U8(1, v1, 0xcc, 0xcc);

  VSET(16, e16, m1);
  VLOAD_16(v2, 0xffff, 0x0000, 0xf0f0, 0x0f0f, 0xffff, 0x0000, 0xf0f0, 0x0f0f, 0xffff, 0x0000, 0xf0f0, 0x0f0f, 0xffff,
           0x0000, 0xf0f0, 0x0f0f);
  VLOAD_16(v3, 0xf2ff, 0x0100, 0xf0f0, 0x0f0f, 0xf2ff, 0x0100, 0xf0f0, 0x0f0f, 0xf2ff, 0x0100, 0xf0f0, 0x0f0f, 0xf2ff,
           0x0100, 0xf0f0, 0x0f0f);
  asm volatile("vmset.m v1");
  asm volatile("vmseq.vv v1, v2, v3");
  VSET(2, e8, m1);
  VCMP_U8(2, v1, 0xcc, 0xcc);

  VSET(16, e32, m2);
  VLOAD_32(v4, 0xffffffff, 0x00000000, 0xf0f0f0f0, 0x0f0f0f0f, 0xffffffff, 0x00000000, 0xf0f0f0f0, 0x0f0f0f0f,
           0xffffffff, 0x00000000, 0xf0f0f0f0, 0x0f0f0f0f, 0xffffffff, 0x00000000, 0xf0f0f0f0, 0x0f0f0f0f);
  VLOAD_32(v6, 0xfff2ffff, 0x01000000, 0xf0f0f0f0, 0x0f0f0f0f, 0xfff2ffff, 0x01000000, 0xf0f0f0f0, 0x0f0f0f0f,
           0xfff2ffff, 0x01000000, 0xf0f0f0f0, 0x0f0f0f0f, 0xfff2ffff, 0x01000000, 0xf0f0f0f0, 0x0f0f0f0f);
  asm volatile("vmset.m v2");
  asm volatile("vmseq.vv v2, v4, v6");
  VSET(2, e8, m1);
  VCMP_U8(3, v2, 0xcc, 0xcc);
};

